// src/App.js
import React, { useState } from 'react';
import RuleList from './components/RuleList';
import EvaluateForm from './components/EvaluateForm';

function App() {
  const [combinedAST, setCombinedAST] = useState(null);  // State to store the combined AST

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <h1 className="text-center text-3xl font-bold text-gray-800">Rule Engine with AST</h1>
      <div className="container mx-auto mt-8">
        <RuleList setCombinedAST={setCombinedAST} />  {/* Pass setCombinedAST to RuleList */}
        {combinedAST && <EvaluateForm combinedAST={combinedAST} />}  {/* Only show EvaluateForm when AST is combined */}
      </div>
    </div>
  );
}

export default App;
