// src/components/RuleForm.js
import React, { useState } from 'react';
import axios from 'axios';

const RuleForm = ({ refreshRules }) => {
  const [ruleName, setRuleName] = useState('');
  const [ruleString, setRuleString] = useState('');
  const [message, setMessage] = useState('');

  // Handle form submission to create a new rule
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/rules/create-rule', {
        ruleName,
        ruleString
      });
      console.log('Rule created:', response.data);
      setMessage('Rule successfully created!');
      setRuleName(''); // Clear the input fields after submission
      setRuleString('');
      
      // Call the refreshRules function to fetch the latest rules
      refreshRules();

    } catch (err) {
      console.error('Error creating rule:', err);
      setMessage('Error creating rule. Please check the rule format.');
    }
  };

  return (
    <div className="bg-white p-4 shadow-md rounded-md max-w-lg mx-auto">
      <h2 className="text-xl font-semibold mb-4">Create New Rule</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Rule Name"
          value={ruleName}
          onChange={(e) => setRuleName(e.target.value)}
          className="block w-full mb-4 border px-4 py-2 rounded-md"
          required
        />
        <textarea
          placeholder="Enter rule (e.g., age > 30 AND salary > 50000)"
          value={ruleString}
          onChange={(e) => setRuleString(e.target.value)}
          className="block w-full mb-4 border px-4 py-2 rounded-md"
          required
        />
        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600"
        >
          Add Rule
        </button>
      </form>
      {message && <p className="mt-4 text-center text-green-600">{message}</p>}
    </div>
  );
};

export default RuleForm;
