// src/components/RuleList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RuleList = ({ setCombinedAST }) => {
  const [rules, setRules] = useState([]);
  const [selectedRules, setSelectedRules] = useState([]);

  // Fetch all available rules from the backend
  useEffect(() => {
    const fetchRules = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/rules');
        setRules(response.data);
      } catch (err) {
        console.error('Error fetching rules:', err);
      }
    };
    fetchRules();
  }, []);

  // Handle combining selected rules
  const handleCombine = async () => {
    if (selectedRules.length < 2) {
      alert('Please select at least two rules to combine.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/rules/combine-rules', {
        ruleIds: selectedRules
      });
      console.log("Combined AST:", response.data.combinedAST);
      setCombinedAST(response.data.combinedAST);  // Pass combined AST to the parent
    } catch (err) {
      console.error('Error combining rules:', err);
    }
  };

  // Handle checkbox change
  const handleCheckboxChange = (ruleId) => {
    if (selectedRules.includes(ruleId)) {
      setSelectedRules(selectedRules.filter(id => id !== ruleId));
    } else {
      setSelectedRules([...selectedRules, ruleId]);
    }
  };
  
  console.log("Combined AST received from backend:", response.data.combinedAST);


  return (
    <div className="bg-white p-4 shadow-md rounded-md max-w-lg mx-auto mt-8">
      <h2 className="text-xl font-semibold mb-4">Select Rules to Combine</h2>
      <p className="text-gray-600 mb-4">Please select at least two rules to combine:</p>
      
      <ul className="list-disc pl-5">
        {rules.map(rule => (
          <li key={rule._id} className="mb-2">
            <label className="flex items-start space-x-2">
              <input
                type="checkbox"
                value={rule._id}
                onChange={() => handleCheckboxChange(rule._id)}
                className="mt-1"
              />
              <div>
                <span className="font-semibold">{rule.ruleName}</span>
                <p className="text-sm text-gray-600">Condition: {rule.ast.left.condition} {rule.ast.type} {rule.ast.right.condition}</p>
              </div>
            </label>
          </li>
        ))}
      </ul>

      <button
        onClick={handleCombine}
        className="w-full bg-blue-500 text-white py-2 mt-4 rounded-md hover:bg-blue-600"
      >
        Combine Selected Rules
      </button>
      

    </div>
    
  );
};

export default RuleList;
