import React, { useState } from 'react';
import axios from 'axios';

const RuleForm = () => {
  const [ruleName, setRuleName] = useState('');
  const [ruleString, setRuleString] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/rules/create-rule', { ruleName, ruleString });
      setRuleName('');
      setRuleString('');
    } catch (err) {
      console.error('Error creating rule:', err);
    }
  };

  return (
    <form className="max-w-lg mx-auto p-4 bg-white shadow-md rounded-md" onSubmit={handleSubmit}>
      <h2 className="text-xl font-semibold mb-4">Create a New Rule</h2>
      <input
        type="text"
        className="block w-full mb-4 border px-4 py-2 rounded-md"
        placeholder="Rule Name"
        value={ruleName}
        onChange={(e) => setRuleName(e.target.value)}
      />
      <textarea
        className="block w-full mb-4 border px-4 py-2 rounded-md"
        placeholder="Enter rule (e.g., age > 30 AND salary > 50000)"
        value={ruleString}
        onChange={(e) => setRuleString(e.target.value)}
      />
      <button className="w-full bg-blue-500 text-white py-2 rounded-md">Create Rule</button>
    </form>
  );
};

export default RuleForm;
