import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RuleList = ({ setCombinedAST }) => {
  const [rules, setRules] = useState([]);
  const [selectedRules, setSelectedRules] = useState([]);

  useEffect(() => {
    const fetchRules = async () => {
      const response = await axios.get('http://localhost:5000/api/rules');
      setRules(response.data);
    };
    fetchRules();
  }, []);

  const handleCombine = async () => {
    const response = await axios.post('http://localhost:5000/api/rules/combine-rules', { ruleIds: selectedRules });
    setCombinedAST(response.data.combinedAST);
  };

  return (
    <div className="max-w-lg mx-auto p-4 bg-white shadow-md rounded-md mt-6">
      <h2 className="text-xl font-semibold mb-4">Available Rules</h2>
      <ul>
        {rules.map((rule) => (
          <li key={rule._id} className="flex items-center mb-2">
            <input
              type="checkbox"
              value={rule._id}
              onChange={(e) => setSelectedRules([...selectedRules, rule._id])}
            />
            <span className="ml-2">{rule.ruleName}</span>
          </li>
        ))}
      </ul>
      <button className="w-full bg-green-500 text-white py-2 mt-4 rounded-md" onClick={handleCombine}>
        Combine Selected Rules
      </button>
    </div>
  );
};

export default RuleList;
